rent_class');function add_current_nav_class($classes, $item) {	// Getting the current post details	global $post;	// Getting the post type of the current post	$current_post_type = get_post_type_object(get_post_type($post->ID));	//If not in a post type get the hell out of here	if( !is_array( $current_post_type_slug = $current_post_type->rewrite ) ){		return $classes;	}	$current_post_type_slug = $current_post_type->rewrite['slug'];	//echo '<pre>';var_dump($current_post_type); echo '</pre>';	// Getting the URL of the menu item	$menu_slug = strtolower(trim($item->url));	// If the menu item URL contains the current post types slug add the current-menu-item class	if (strpos($menu_slug,$current_post_type_slug) !== false) {		$classes[] = 'current-menu-item';	}	// Return the corrected set of classes to be added to the menu item	return $classes;}add_action('nav_menu_css_class', 'add_current_nav_class', 10, 2 );//Run before html is outputfunction before_html(){	$page_object = get_queried_object();	$page_id = get_queried_object_id();}add_action('before_html', 'before_html');// custom excerpt lengthfunction custom_excerpt_length($length) {	return 20;}add_filter('excerpt_length', 'custom_excerpt_length');?>